$(function () { $("#burgerBtn").on("click", function (a) { a.preventDefault(); let b = { burger_name: $("#userBurger").val().trim(), devoured: 0 }; $.ajax("/api/burgers", { type: "POST", data: b }).then(function () { console.log("New Burger Added..."), location.reload() }) }), $(".devourBtn").on("click", function () { let a = $(this).data("id"), b = { devoured: 1 }; $.ajax("api/burgers/" + a, { type: "PUT", data: b }).then(function () { console.log(`You have ${b} devoured ${a}...`), location.reload() }) }), $(".deleteBtn").on("click", function () { let a = $(this).data("id"); $.ajax("api/burgers/" + a, { type: "DELETE" }).then(function () { console.log(`You have deleted ${a}...`), location.reload() }) }) });